package UNIDAD1;

public class variablemasmasmenosmenos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Haz un programa que muestre el comportamiento de ++x y x++ con x = 5.
		   int x = 5;
		   
		     System.out.println("Valor inicial de x: " + x);

		        System.out.println("Resultado de ++x: " + ++x); 

		        System.out.println("Valor de x después de ++x: " + x);

		        System.out.println("Resultado de x++: " + x++); 

		        System.out.println("Valor de x después de x++: " + x);

	}

}
